import React, { useState } from 'react';
import './Trending.css';  // Ensure this CSS contains appropriate styling for this component
import data_product from '../Assets/data';  // Your data source
import Knife from '../Knife/Knife';  // Your Knife component

const ITEMS_PER_PAGE = 30;

export const Trending = () => {
    const [currentPage, setCurrentPage] = useState(1);

    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;
    const currentItems = data_product.slice(startIndex, endIndex);

    const totalPages = Math.ceil(data_product.length / ITEMS_PER_PAGE);

    const handlePrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    return (
        <div className='trending'>
            <div className='searchbar'></div>
            <h1>Trending Knives</h1>
            <div className='trending-items'>
                {currentItems.map((knife, index) => (
                    <Knife key={index} id={knife.id} name={knife.name} image={knife.image}
                           knife_price={knife.knife_price}/>
                ))}
            </div>
            <div className='pagination'>
                <button onClick={handlePrevPage} disabled={currentPage === 1}>Previous</button>
                <span>Page {currentPage} of {totalPages}</span>
                <button onClick={handleNextPage} disabled={currentPage === totalPages}>Next</button>
            </div>
        </div>
    );
};

export default Trending;
